import './style.css';
import setaUp from '../../../assests/setaFiltro.svg';
import setaDown from '../../../assests/setaBaxio.svg';
import { useState } from 'react'


function HeaderList() {
    const [filter, setFilter] = useState('date');
    const [order, setOrder] = useState('asc');
    function handleCahngeFiulter(type) {
        setFilter(type);
    }
    return (
        <div className="table-head">
            <div className="column-title cursor-pointer"
                onClick={() => handleCahngeFiulter('date')}
            >
                <span>Data</span>
                <img src={filter === 'date' && setaDown} alt="seta-filtro" />
            </div>
            <div className="column-title cursor-pointer"
            onClick={() => handleCahngeFiulter('weekDay')}
            >
                <span>Dia da semana</span>
                <img src={filter === 'weekDay' && setaDown} alt="seta-filtro" />
            </div>
            <div className="column-title">
                <span>Categoria</span>
            </div>
            <div className="column-title cursor-pointer">
                <span> Valor</span>
                <img src={filter === 'value' && setaDown} alt="seta-filtro" />
            </div>
            <div className="column-title"></div>
        </div>
    )

};

export default HeaderList;
