import './style.css';
import close from '../../assests/closeBtn.svg';
import { useState } from 'react';

const defaultValuesForm = {
    value: 0,
    category: '',
    description: ''
}


function ModalRegister() {
    const [active, setActive] = useState('debit');
    const [form, setForm] = useState();
    function handleChange(target) {
        setForm({ ...form, [target.name]: target.value })
    }

    return (
        <div className='backdrop'>
            <div className='modal-container modal-storage' alt='close'><img src={close} className='close-icon' />
                <h2>Adcionar Registro</h2>
                <div className='container-buttons'>
                    <button className={`btn-ent ${active === 'credit' && 'credit'}`} onClick={() => setActive('credit')}>Entrada</button>
                    <button className={`btn-ent ${active === 'debit' && 'debit'}`} onClick={() => setActive('debit')}>Saída</button>
                </div>
                <form>
                    <div>
                        <label htmlFor='valor'>Valor</label>
                        <input
                            id='valor'
                            type='number'
                            name='value'
                            value={form.value}
                            onChange={(event) => handleChange(event.target)} />
                    </div>
                    <div>
                        <label htmlFor='categoria'>Categoria</label>
                        <input
                            id='categoria'
                            type='text'
                            name='category'
                            value={form.category}
                            onChange={(event) => handleChange(event.target)}
                        />
                    </div>
                    <div>
                        <label htmlFor='data'>Data</label>
                        <input
                            id='data'
                            type='text'
                            name='description'
                            value={form.description}
                            onChange={(event) => handleChange(event.target)}
                        />
                    </div>
                    <div>
                        <label htmlFor='descricao'>Descrição</label>
                        <input
                            id='descricao'
                            type='text'
                            name='date'
                            value={form.date}
                            onChange={(event) => handleChange(event.target)}
                        />
                    </div>
                    <div className='container-btn-insert'>
                        <button>Confirmar</button>
                    </div>
                </form>
            </div>
        </div>
    )
};

export default ModalRegister;
