import { useState } from 'react';
import { useEffect } from 'react/cjs/react.development';
import './style.css';


function Resume({ transactions }) {
    const [resume, setResume] = useState({ credit: 0, debit: 0, balance: 0 });

    useEffect(() => {
        const somaCredit = transactions.reduce((acum, item) => {
            return item.type === 'credit' ? acum + item.value : acum + 0;
        })
    }, [transactions])
    return (
        <div className='container-resume'>
            <h3>Resumo</h3>
            <div >
                <span>Entradas</span>
                <strong className='in'>R$</strong>

            </div>
            <div>
                <span>Saidas</span>
                <strong className='out'>R$</strong>

            </div>
            <div className='horizontal-line'></div>
            <div>
                <span>Saldo</span>
                <strong className='balance'>R$</strong>

            </div>
        </div>

    )
};

export default Resume;
