import weekDayMap from './weekMap';

export function orderColumnAsc(a, b, por) {
    if (por === 'date') {
        return new Date(a.date) - new Date(b.date);
    }

    if (por === 'week_day') {
        return weekDayMap[a.week_day] - weekDayMap[b.week_day]
    }

    if (por === 'value') {
        return a.value - b.value

    }
}

export function orderColumnDesc(a, b, por) {
    if (por === 'date') {
        return new Date(b.date) - new Date(a.date);
    }

    if (por === 'weekDay') {
        return weekDayMap[b.week_day] - weekDayMap[a.week_day]
    }

    if (por === 'value') {
        return b.value - a.value

    }
}
